mapper.select:
/* getAuthUsers */
select
  idx, userid, usergrant, username, usercompany, regdate, regid, stat, userpass as password
from auth_user
/* where stat != 'Y' */
order by regdate desc

/* getAuthUserById */
select
idx, userid, usergrant, username, usercompany, regdate, regid, stat, userpass as password
from auth_user
where userid = #{userid}
order by regdate DESC limit 1

mapper.update:
/* uptAuthUser */
update auth_user
set
regdate = now()
,regid = #{regid}

    , usergrant = #{usergrant}


    , username = #{username}


    , usercompany = #{usercompany}


    , stat = #{stat}


    , userpass = #{password}

where userid = #{userid}

/* delAuthUser */
update auth_user
set
regdate = now()
,regid = #{regid}
, stat = 'D'
where userid = #{userid}
