      , ps_tag = #{ps_tag}
  

    , ps_type = #{ps_type}


    , dest_field = #{dest_field}


    , descriptp = #{descriptp}


    , ps_add_url = #{ps_add_url}


    , dest_charset = #{dest_charset}

where ps_id=#{ps_id}
