mapper.select:
/* getDicAddWordsPaging */
select
idx, word, freq, regdate, regid
from dic_add_words
order by regdate DESC
limit ${pageSize} offset ${offsetIdx}

mapper.insert:
/* insDicAddWords */
insert into dic_add_words (word, freq, regdate, regid)
values (#{word}, #{freq}, now() , #{regid})

mapper.update:
/* uptDicAddWords */
update dic_add_words
  set
    regdate = now()
    ,regid = #{regid}
    
      , word = #{word}
    
    
      , freq = #{freq}
    
  where idx = #{idx}

/* uptDicAddWordsByWord */
update dic_add_words
set
regdate = now()
,regid = #{regid}

    , word = #{word}


    , freq = #{freq}

where word = #{oldword}

mapper.delete:
/* delDicAddWordsByWord */
delete from dic_add_words
where word = #{oldword}
