/* ---- 우측하단 통계 ----- */
/* 주간통계 */
select 1

    /* 일별통계 */
    /* 인입건수 */
        /* down_countsInserted */
        select count(*) as cnt, date from (
            select a.cid, a.date from (
                (
                select cc.content_id as cid, DATE(cc.regdate) as date from ccube_contents cc
                where
                DATE(cc.regdate)  >=  STR_TO_DATE(#{sdate}, '%Y-%m-%d')
                AND DATE(cc.regdate)  <=  STR_TO_DATE(#{edate}, '%Y-%m-%d')
                )
                union
                (
                select cs.series_id as cid, DATE(cs.regdate) as date from ccube_series cs
                where
                DATE(cs.regdate)  >=  STR_TO_DATE(#{sdate}, '%Y-%m-%d')
                AND DATE(cs.regdate)  <=  STR_TO_DATE(#{edate}, '%Y-%m-%d')
                )
            ) a
        ) b group by b.date

/* getCountInsertedDaily */
select sum(a.cnt) as cnt_all from (
    (
    select count(*) as cnt from ccube_contents
    where regdate  >=  STR_TO_DATE(#{sdate}, '%Y-%m-%d 00:00:00')
    and regdate  <=  STR_TO_DATE(#{edate}, '%Y-%m-%d 23:59:59')
    ) union (
    select count(*) as cnt from ccube_series
    where regdate  >=  STR_TO_DATE(#{sdate}, '%Y-%m-%d 00:00:00')
    and regdate  <=  STR_TO_DATE(#{edate}, '%Y-%m-%d 23:59:59')
    )
) a
