select count(*) from items_stat iss
inner join items it on it.idx = iss.idx
inner join ccube_keys ck on ck.itemidx = it.idx and (ck.content_id = it.cid or ck.series_id = it.cid)
where iss.stat in ('FR','FA');

/* 승인대기 건수 */
